import React, { Component } from "react";
import "../CSS/donor.css";

import axios from "axios";

export default class addDonors extends Component {
  constructor(props) {
    super(props);

    this.state = {
      category: [],
      Centername: "",
      CenterManagername: "",
      ccontact:"",
      address: "",
      candidatename: "",
      candidateadhar: "",
      age: "",
      cat: "",
      gender: "",
      contact: "",
      experience: "",
      salaryday: "",

     file: null,
    };
    this.onChange = this.onChange.bind(this);
    this.handleDropdownChange = this.handleDropdownChange.bind(this);
  }
  componentDidMount = async () => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.get(
        "http://localhost:5000/api/v1/category/",
        config
      );
      this.setState({
        category: res.data.data,
      });
      console.log(this.state.category);
    } catch (err) {
      console.log("Can't load the items");
    }
  };
  // Input on change
  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  // Dropdown change
  handleDropdownChange = (e) => {
    this.setState({ cat: e.target.value });
    console.log(e.target.value);
  };
  // fileupload
  onChangeHandler = (e) => {
    this.setState({
      file: e.target.files[0],
    });
    console.log(this.state.file);
  };
  onSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append("file", this.state.file, this.state.file.name);

    console.log(data);
    const token = sessionStorage.getItem("token");
    const config = {
      headers: {
        Authorization: `Bearer ${token}`,
        "Content-Type": "multipart/form-data",
      },
    };
    try {
      const res = await axios.post(
        `http://localhost:5000/api/v1/center/photo`,
        data,
        config
      );
      console.log(res.data.data);

      const centers = {
        Centername: this.state.Centername,
        CenterManagername: this.state.CenterManagername,
        ccontact:this.state.ccontact,
        address: this.state. address,
        candidatename: this.state. candidatename,
        candidateadhar: this.state.candidateadhar,
        age: this.state.age,
        category:this.state.category,
        gender: this.state.gender,
        contact:this.state.contact,
        experience: this.state.experience,
        salaryday: this.state.salaryday,
        photo: res.data.data
      };
      const body = JSON.stringify(centers);
      console.log(body);
      const config1 = {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      };
      const result = await axios.post(
        `http://localhost:5000/api/v1/center`,
        body,
        config1
      );
      console.log(result);
      alert(`Centers Details Added ${result.data.data.name}`);
    } catch (err) {
      // console.log("Can't load the items");
    }
  };

  render() {
    return (
      <div className="container itmtop">
      {console.log(this.state)}
    <div className="">
      {/* <div className="jumbotron col-md-6 col-sm-5 " id="login-first"></div> */}
      <div className="" id="login-second">
        <div class="page-wrapper p-t-50 p-b-50">
          <div class="wrapper wrapper--w900 ">
            <div class="card cardH card-6 ">
              <div class="card-heading m-4">
                <h2 class="title text-dark">Add Job</h2>
              </div>
              <div class="card-body">
                <form onSubmit={this.onSubmit}
                  encType="multipart/form-data"
                >
                  <div class="form-row frow">
                    <div class="name">Center name:</div>
                    <div class="value">
                    <input
                        className="input--style-6"
                        type="text"
                        name="Centername"
                        value={this.state.Centername}
                        onChange={this.onChange}
                      />
                    </div>
                  </div>

                  <div class="form-row frow">
                    <div class="name">Center Manager name:</div>
                    <div class="value">
                    <input
                        className="input--style-6"
                        type="text"
                        name="CenterManagername"
                        value={this.state.CenterManagername}
                        onChange={this.onChange}

                      />
                    </div>
                  </div>
                  <div class="form-row frow">
                    <div class="name">Center Contact:</div>
                    <div class="value">
                    <input
                        className="input--style-6"
                        type="text"
                        name="ccontact"
                        value={this.state.ccontact}
                        onChange={this.onChange}

                      />
                    </div>
                  </div>


                  <div class="form-row frow">
                    <div class="name">Center Location</div>
                    <div class="value">
                    <input
                        className="input--style-6"
                        type="text"
                        name="address"
                        placeholder=""
                        value={this.state.address}
                        onChange={this.onChange}
                      />
                    </div>
                  </div>

                  <div class="form-row frow">
                  <div class="name">Candidate Name</div>
                    <div class="value">

                    <input
                        className="input--style-6"
                        type="text"
                        name="candidatename"
                        placeholder=""
                        value={this.state.candidatename}
                        onChange={this.onChange}
                      />
                      </div>
                    </div>


                  <div class="form-row frow">
                    <div class="name">Candidate adhaar</div>
                    <div class="value">

                    <input
                        className="input--style-6"
                        type="text"
                        name="candidateadhar"
                        placeholder=""
                        value={this.state.candidateadhar}
                        onChange={this.onChange}
                      />
                      </div>
                    </div>
                    <div class="form-row frow">
                    <div class="name">Candidate age:</div>
                    <div class="value">
                    <input
                        className="input--style-6"
                        type="text"
                        name="age"
                        placeholder=""
                        value={this.state.age}
                        onChange={this.onChange}
                      />
                    </div>
                  </div>
                  <div className="form-row frow">
                    <div className="category">Select  CandidateCategory:</div>
                    <div className="value">
                    <select
                      id="dropdown "
                      className="btn bg-success"
                      onChange={this.handleDropdownChange}
                    >
