import React, { Component } from "react";
import "../CSS/donor.css";
import axios from "axios";
// import Dropdown from "react-bootstrap/Dropdown";

export default class addDonors extends Component {
  constructor(props) {
    super(props);

    this.state = {
      category: [],
      centername: "",
      centermname: "",
      centerlocation: "",
      candidatename: "",
      candidateadhar: "",
      candidateage: "",
      category: "",
      gender: "",
      experience: "",
      salary: "",
      contact: "",
     file: null,
    };
    this.onChange = this.onChange.bind(this);
    this.handleDropdownChange = this.handleDropdownChange.bind(this);
  }
  componentDidMount() {
    this.props.getCategory();
  }
  // Input on change
  onChange(e) {
    this.setState({
      [e.target.name]: e.target.value,
    });
  }
  // Dropdown change
  handleDropdownChange(e) {
    this.setState({ category: e.target.value });
  }
  // fileupload
  onChangeHandler = (e) => {
    this.setState({
      file: e.target.files[0],
    });
  };
  onSubmit = async (e) => {
    e.preventDefault();
    // const data = new FormData();
    // data.append("file", this.state.file, this.state.file.name);

    // console.log(data);
    // const token = sessionStorage.getItem("token");
    // const config = {
    //   headers: {
    //     Authorization: `Bearer ${token}`,
    //     "Content-Type": "multipart/form-data",
    //   },
    // };
    try {
      // const res = await axios.post(
      //   `http://localhost:5000/api/v1/vendors/products/photo`,
      //   data,
      //   config
      // );
      // console.log(res.data.data);

      const jobs = {
        centername: this.state.centername,
        centermname: this.state.centermname,
        centerlocation: this.state.centerlocation,
        candidatename: this.state.candidatename,
        candidateadhar: this.state.candidateadhar,
        candidateage: this.state.candidateage,
        category:this.state.category,
        gender: this.state.gender,
        experience: this.state.experience,
        salary: this.state.salary,

        // photo: res.data.data,
      };
      const body = JSON.stringify(jobs);
      console.log(body);
      const token = sessionStorage.getItem("token");
      const config1 = {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      };
      const result = await axios.post(
        `http://localhost:5000/api/v1/Job`,
        body,
        config1
      );
      console.log(result.data.data);
      alert(`candiate Addred ${result.data.data.centername}`);
    } catch (err) {
      // console.log("Can't load the items");
    }
  };
  render() {
    return (
      <div className="container itmtop">
        <div className="">
          {/* <div className="jumbotron col-md-6 col-sm-5 " id="login-first"></div> */}
          <div className="" id="login-second">
            <div class="page-wrapper p-t-50 p-b-50">
              <div class="wrapper wrapper--w900 ">
                <div class="card cardH card-6 ">
                  <div class="card-heading m-4">
                    <h2 class="title text-dark">Add Job</h2>
                  </div>
                  <div class="card-body">
                    <form onSubmit={this.onSubmit}
                      encType="multipart/form-data"
                    >
                      <div class="form-row frow">
                        <div class="name">Center name:</div>
                        <div class="value">
                          <input
                            class="input--style-6"
                            type="text"
                            name="iname"
                          />
                        </div>
                      </div>

                      <div class="form-row frow">
                        <div class="name">Center Manager name:</div>
                        <div class="value">
                          <input
                            class="input--style-6"
                            type="text"
                            name="iname"
                          />
                        </div>
                      </div>



                      <div class="form-row frow">
                        <div class="name">Center Location</div>
                        <div class="value">
                          <input
                            class="input--style-6"
                            type="text"
                            name="full_name"
                          />
                        </div>
                      </div>

                      <div class="form-row frow">
                      <div class="name">Candidate Name</div>
                        <div class="value">

                            <input
                              class="input--style-6"
                              type="text"
                              name="desc"
                              placeholder=""
                            />
                          </div>
                        </div>


                      <div class="form-row frow">
                        <div class="name">Candidate adhaar</div>
                        <div class="value">

                            <input
                              class="input--style-6"
                              type="number"
                              name="adhaar"
                              placeholder=""
                            />
                          </div>
                        </div>
                        <div class="form-row frow">
                        <div class="name">Candidate age:</div>
                        <div class="value">
                          <input
                            class="input--style-6"
                            type="number"
                            name="iname"
                          />
                        </div>
                      </div>
                      <div className="form-row frow">
                        <div className="name">Select  CandidateCategory:</div></div>
                        {/* <Dropdown>
                          <Dropdown.Toggle
                            variant="success"
                            id="dropdown-basic"
                          >
                            Category
                          </Dropdown.Toggle>

                          <Dropdown.Menu>
                            {this.props.category.map((category) => (
                              <Dropdown.Item key={category._id}>
                                {category.catname}
                              </Dropdown.Item>
                            ))} */}

                            {/* <Dropdown.Item href="#/action-2">
                              Another action
                            </Dropdown.Item>
                            <Dropdown.Item href="#/action-3">
                              Something else
                            </Dropdown.Item> */}
                          {/* </Dropdown.Menu>
                        </Dropdown>
                      </div> */}

                       <div class="form-row frow">
                        <div class="name">Candidate Gender</div>
                        <div class="value">
                          <div class="input-group">
                            <input
                              class="input--style-6"
                              type="email"
                              name="email"
                              placeholder=""
                            />
                          </div>
                        </div>
                      </div>
                      <div class="form-row frow">
                        <div class="name">Candidate Experience</div>
                        <div class="value">
                          <div class="input-group">
                            <input
                              class="input--style-6"
                              type="email"
                              name="email"
                              placeholder=""
                            />
                          </div>
                        </div>
                      </div>
                      <div class="form-row frow">
                        <div class="name">Candidate Salary/Day</div>
                        <div class="value">
                          <div class="input-group">
                            <input
                              class="input--style-6"
                              type="email"
                              name="email"
                              placeholder=""
                            />
                          </div>
                        </div>
                      </div>

                      <div class="form-row frow">
                        <div class="name">Contact</div>
                        <div class="value">
                          <div class="input-group">
                            <input
                              class="input--style-6"
                              type="email"
                              name="email"
                              placeholder=""
                            />
                          </div>
                        </div>
                      </div>
                      <div class="form-row frow">
                        <div class="name">Upload Image:</div>
                        <div class="value">
                          <div class="input-group js-input-file">
                            <input
                              class="input-file"
                              type="file"
                              name="file_doc"
                              id="file"
                            />
                            <label class="label--file" for="file">
                              Choose file
                            </label>
                            <span class="input-file__info">No file chosen</span>
                          </div>
                          <div class="label--desc">
                            Upload your Document/Id proff or any other relevant
                            file. Max file size 50 MB
                          </div>
                        </div>
                      </div>




                      {/* <div class="form-row frow">
                        <div class="name">Fees/Monthly</div>
                        <div class="value">
                          <div class="input-group">
                            <input
                              class="input--style-6"
                              type="email"
                              name="email"
                              placeholder=""
                            />
                          </div>
                        </div>
                      </div>
                   */}
                    </form>
                  </div>
                  <div class="card-footer">
                    <button class="btn btn--radius-2 btn-gray" type="submit">
                      ADD
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
